import React, { Component } from 'react';
import Header from '../../partials/header';
import Navbar from '../../partials/navbar'
import BreadCrumb from '../../partials/breadcrumb'
import {fetchAllUsers,unBlockUser,blockUser} from './dashboard-api'
import{tasks} from './dashboard-datasource'
// import {hasAuthority} from  '../../utils/api-utils'

import {
    Form,
    Input,
    Tooltip,
    Icon,
    Cascader,
    Select,
    Row,
    Col,
    Spin,
    notification,Popconfirm,Table, Divider, Tag 
  } from 'antd';

const FormItem = Form.Item;
class DashboarPage extends Component  {
    constructor(props) {
        super(props);
        this.state={
            tasks:[],
            name:"",
            description:"",
            status:"",
            completedDate:"",
            isloading:true
        }
            // this.onsubmitDrug = this.onsubmitDrug.bind(this);

    }

    componentDidMount(){
        this.getAllTask()
    } 

    getTaskStatus = (status)=>{
        let badge ={}
        if(status=="Completed"){
            badge = {clazz:"badge badge-success", display:"Completed"}
        } 
        else if(status=="pending"){
            badge = {clazz:"badge badge-info", display:"Pending"}
        }
        else if(status=="started"){
            badge = {clazz:"badge badge-primary", display:"Started"}
        }
        else if(status=="cancelled"){
            badge = {clazz:"badge badge-danger", display:"Cancelled"}
        }  
        return (
            <span className={badge.clazz}>
                {badge.display}
            </span>
        );                  
    }

    getTableHeader = ()=>{

        const columns = [
            {
              title: 'Name',
              dataIndex: 'name',
              key: 'name',
            },
            {
                title: 'Description',
                dataIndex: 'description',
                key: 'description',
            },
            {
                title: 'Status',
                dataIndex: 'status',
                key: 'status',
                render: status => (
                    <span>
                        {this.getTaskStatus(status)}  
                    </span>
                  ),
            },
            {
                title: 'Date completed',
                dataIndex: 'completionDate',
                key: 'completionDate',
            },

            // {
            //     title: 'Action',
            //     key: 'action',
            //     render: (text, record) => (
            //       <span>
            //         {
            //             hasAuthority(["ROLE_ADMIN_BLOCK_USER"]) 
            //             ?

            //                 (record.block==0) ?

            //                 <Popconfirm
            //                     title="Are you sure you want to Block this User?"
            //                     onConfirm={(e)=>{this.blockUzer(record.email)}}
            //                     onCancel={this.cancelDrug()}
            //                     okText="Yes"
            //                     cancelText="No"
            //                 >
            //                     <button type="button" style={{marginBottom:'5px'}}  class="btn btn-danger btn-sm">Block</button>
            //                 </Popconfirm>

            //                 :

            //                 <Popconfirm
            //                     title="Are you sure you want to UnBlock this User?"
            //                     onConfirm={(e)=>{this.unblockUzer(record.email)}}
            //                     onCancel={this.cancelDrug()}
            //                     okText="Yes"
            //                     cancelText="No"
            //                 >
            //                     <button type="button" style={{marginBottom:'5px'}}  class="btn btn-primary btn-sm">UnBlock</button>
            //                 </Popconfirm>
            //             :
            //             ""

            //         }

            //         {/* <button type="button" style={{marginBottom:'5px'}}  class="btn btn-primary btn-sm">Block</button> */}
            //         <Divider type="vertical" />
            //         <button onClick={(e)=>{this.props.history.push(`/single-user/${record.id}`);}} type="button" class="btn btn-primary btn-sm">View Audit Trail</button>
            //       </span>
            //     ),
            //   },
          ];

        return columns
    }




    getAllTask = () =>{
        this.setState({isloading:true})
        this.setState({tasks:tasks})
        this.setState({isloading:false})

    }



  render() {
    const bodystyle =  {
        background: "#f1f2f7",
        display: "table",
        fontFamily: "'Open Sans' sans-serif !important",
        fontSize: "16px",
        width: "100%" }

    return (
        <div style={bodystyle}>
        {/* <!-- Left Panel --> */}

            <Navbar></Navbar>
            {/* <!-- /#left-panel --> */}
            {/* <!-- Left Panel --> */}

            {/* <!-- Right Panel --> */}

            <div  class="right-panel ">
                <Header></Header>
                {/* <!-- /header --> */}
                {/* <!-- Header--> */}

                <BreadCrumb menu="User" submenu=" "></BreadCrumb>

                <div class="content mt-3">

                    <div class="animated fadeIn">
                        <div className="row">
                            <Form onSubmit={this.handleSubmit} className="signup-form">
                                <FormItem
                                    label="Name"
                                    // validateStatus={this.state.name.validateStatus}
                                    // help={this.state.name.errorMsg}
                                    >
                                    <Input
                                        size="large"
                                        name="name"
                                        autoComplete="off"
                                        placeholder="Drug name"
                                        value={this.state.name.value}
                                        onChange={(event) => this.handleInputChange(event, this.validateInput)}/>
                                </FormItem>

                                <FormItem
                                    label="Amount"
                                    // validateStatus={this.state.amount.validateStatus}
                                    // help={this.state.amount.errorMsg}
                                    >
                                    <Input
                                        size="large"
                                        name="amount"
                                        autoComplete="off"
                                        placeholder="Amount in number e.g 100"
                                        value={this.state.description.value}
                                        onChange={(event) => this.handleInputChange(event, this.validateInput)}/>
                                </FormItem>

                                <FormItem
                                    label="Alias"
                                    // validateStatus={this.state.alias.validateStatus}
                                    // help={this.state.alias.errorMsg}
                                    >
                                    <Input
                                        size="large"
                                        name="alias"
                                        autoComplete="off"
                                        placeholder="Alias e.g Pz"
                                        value={this.state.status.value}
                                        onChange={(event) => this.handleInputChange(event, this.validateInput)}/>
                                </FormItem>

                                <FormItem
                                    label="Alias"
                                    // validateStatus={this.state.alias.validateStatus}
                                    // help={this.state.alias.errorMsg}
                                    >
                                    <Input
                                        size="large"
                                        name="alias"
                                        autoComplete="off"
                                        placeholder="Alias e.g Pz"
                                        value={this.state.completionDate.value}
                                        onChange={(event) => this.handleInputChange(event, this.validateInput)}/>
                                </FormItem>

                            </Form>
                        </div>
                        <Spin spinning={this.state.isloading}>

                            {/* Table for drugs */}
                            <div class="row">

                            <div className="col-md-12">
                                <div className="card">
                                    <div className="card-header">
                                        <strong className="card-title">Data Table</strong>
                                    </div>
                                    <div className="card-body">
                                        <Table columns={this.getTableHeader()} dataSource={this.state.tasks} />
                                    </div>

                                </div>
                            </div>


                        </div>

                        </Spin>

                    </div>
                </div>
                {/* <!-- .content --> */}
            </div>
            {/* <!-- /#right-panel --> */}

            {/* <!-- Right Panel --> */}
        </div>
    );
  }
}

export default DashboarPage;
